import DrumPad from "./drumPad";
import { useDispatch, useSelector } from "react-redux";
import "./main.css";
import { Container } from "react-bootstrap";
import $ from "jquery";

const Main = () => {
  const dispatch = useDispatch();
  const sounds = useSelector((state) => state.sounds.sounds);
  const letters = ["Q", "W", "E", "A", "S", "D", "Z", "X", "C"];
  const display = useSelector((state) => state.display);
  const changeVolume = (e) => {
    const volume = e.target.value;
    const text = $("#display").text();
    $("#display").text("Volume: " + Math.round(volume * 100) + "%");
    setTimeout(() => {
      $("#display").text(text);
    }, 1000);
    dispatch({ type: "VOLUME", payload: volume });
  };
  return (
    <Container id="drum-machine" className="main">
      <div id="drum-pad-section">
        {sounds.map((sound, index) => (
          <DrumPad
            name={sound.name}
            key={index}
            letter={letters[index]}
            index={index}
            src={sound.src}
          />
        ))}
      </div>
      <div id="display">{display}</div>
      <div id="slider-container">
        <input
          type="range"
          id="volume"
          name="volume"
          min="0"
          max="1"
          step="0.01"
          onChange={changeVolume}
        />
      </div>
    </Container>
  );
};

export default Main;
