import { Button } from "react-bootstrap";
import "./drumpad.css";
import $ from "jquery";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";

const DrumPad = ({ isOn, letter, index, src, name }) => {
  const dispatch = useDispatch();
  const volume = useSelector((state) => state.volume);
  let fired = false;
  const playSound = () => {
    if (fired) return;

    $("#button" + index).addClass("active");
    setTimeout(() => {
      $("#button" + index).removeClass("active");
    }, 100);

    if (isOn) {
      dispatch({ type: "DISPLAY", payload: name });
      const audio = document.getElementById(letter);
      audio.volume = volume;
      audio.play().then(() => {
        audio.currentTime = 0;
      });
    }
  };
  const fire = () => {
    fired = !fired;
  };
  useEffect(() => {
    return () => {
      $(document).on("keydown", (e) => {
        if (e.key.toUpperCase() === letter) {
          playSound();
          fire();
          console.log(fired);
        }
      });

      $(document).on("keyup", (e) => {
        if (e.key.toUpperCase() === letter) {
          fire();
        }
      });
    };
  }, []);

  return (
    <button
      id={"button" + index}
      type="button"
      className="btn drum-pad"
      onClick={() => {
        playSound();
      }}
    >
      {letter}
      <audio
        id={letter}
        typeof="audio/wav"
        className="drum-pad__audio clip"
        src={src}
      />
    </button>
  );
};

export default DrumPad;
