import { Button } from "react-bootstrap";
import "./drumpad.css";
const DrumPad = ({ letter, index, src }) => {
  return (
    <button
      id={"button" + index}
      type="button"
      className="btn btn-blue drum-pad"
      onClick={() => {
        const audio = document.getElementById("audio" + index);
        audio.currentTime = 0;
        audio.play();
      }}
    >
      {letter}
      <audio id={"audio" + index} className="drum-pad__audio" src={src} />
    </button>
  );
};

export default DrumPad;
