import { Button } from "react-bootstrap";
import "./drumpad.css";
import $ from "jquery";
import { useDispatch, useSelector } from "react-redux";

const DrumPad = ({ letter, index, src, name }) => {
  const dispatch = useDispatch();
  const isOn = useSelector((state) => state.on);
  const volume = useSelector((state) => state.volume);
  const playSound = () => {
    $("#button" + index).addClass("active");
    setTimeout(() => {
      $("#button" + index).removeClass("active");
      if (isOn) {
        dispatch({ type: "DISPLAY", payload: name });
        const audio = document.getElementById(letter);
        audio.volume = volume;
        if (audio.currentTime > 0) {
          audio.currentTime = 0;
          audio.play();
        }
      }
    }, 100);
  };

  $(document).keydown(function (e) {
    if (e.key === letter.toLowerCase()) {
      playSound();
    }
  });
  return (
    <button
      id={"button" + index}
      type="button"
      className="btn btn-blue drum-pad"
      onClick={() => {
        playSound();
      }}
    >
      {letter}
      <audio
        id={letter}
        typeof="audio/wav"
        className="drum-pad__audio clip"
        src={src}
      />
    </button>
  );
};

export default DrumPad;
